import React, {useEffect, useState} from 'react';
import './converter.scss'
import Select from "../UI/Select/Select";
import MultipleAddonsExample from "../UI/MultipleAddonsExample/MultipleAddonsExample";
import arrows from '../imgs/21300.png'


function Converter({getCurrency, Rate}) {
    const [to,setTo] = useState('UAH')
    const [from, setFrom] = useState('USD')
    const [cashTo, setCashTo] = useState('')
    const [cashFrom, setCashFrom] = useState('1')

    useEffect(() =>{
        getCurrency(to, from)
        setTimeout(setTo(Rate),100)
}
        ,[cashTo])

    function NewFrom(event) {
        setFrom(event)
        getCurrency(to,event)
    }

    function NewCashFrom(event) {
        if(event === null){
            setCashFrom(cashFrom.slice(0,cashFrom.length -1 ))
        }
        else if (['1','2','3','4','5','6','7','8','9','0'].includes(event)) {
            setCashFrom((perv) => perv + String(event))}
    }

    function NewTo(event) {
        setTo(event)
        getCurrency(event, from)

    }
    function NewCashTo(event) {
        if(event === null){
            setCashTo(cashTo.slice(0,cashTo.length -1 ))
        }
        else if (['1','2','3','4','5','6','7','8','9','0'].includes(event)) {
            setCashTo((perv) => perv + String(event))}
    }

    return (
        <div className={'converter'}>
            <div >
                <Select
                    Value={from}
                    NewValue={NewFrom}/>
                <MultipleAddonsExample
                    Cash={cashFrom}
                    NewCash={NewCashFrom}
                />
            </div>
            <div className={'arrows'}><img  src={arrows}/></div>
            <div>
                <Select
                    Value={to}
                    NewValue={NewTo}/>
                <MultipleAddonsExample
                    Cash={cashTo}
                    NewCash={NewCashTo}
                />
            </div>
        </div>
    );
}

export default Converter;
