import './App.scss';
import React from "react";
import Header from "./components/Header/Header";
import {useState} from "react";
import axios from "axios";

const listCurrency = ['USD', 'EUR','UAH']



function App() {
    const [currency, setCurrency] = useState()
    const [loading, setLoading] = useState(true)

    const [usduah,setUsdUah] = useState(0);
    const [euruah, setEurUah] = useState(0);

    React.useEffect(() => {
        getUsdUah(),getEurUah()

    }, [])

    function getUsdUah() {
        setUsdUah(getCurrency('uah', 'usd'))
    }
    function getEurUah() {
        setEurUah(getCurrency('uah', 'eur'))
    }

    async function getCurrency(to,from, amount=1) {
        try {
            setLoading(true)
            let response = await axios.get(`https://api.apilayer.com/currency_data/convert?to=${to}&from=${from}&amount=${amount}`,{
                headers : 'apikey: AIUIvPfNpQoLLreRNcFBBUEQAuvUv6Yw'
            })
            (response.data)
            setLoading(false)

        } catch (err) {
            console.log(err)
        }
        return response.data
    }
  return (
    <div className="App">
      <Header
      USDUAH = {usduah}
      EURUAH = {euruah}
      />

    </div>
  );
}

export default App;
