import './App.scss';
import React from "react";
import Header from "./components/Header/Header";
import {useState} from "react";
import axios from "axios";

const listCurrency = ['USD', 'EUR','UAH']



function App() {
    const [currency, setCurrency] = useState()
    const [loading, setLoading] = useState(true)

    const [usduah,setUsdUah] = useState(0);
    const [euruah, setEurUah] = useState(0);

    React.useEffect(() => {
        getCurrency()


    }, [])
    async function getCurrency() {
        try {
            setLoading(true)
            let response = await axios.get("https://api.apilayer.com/currency_data/convert?to=uah&from=usd&amount=1",{
                headers : 'apikey: AIUIvPfNpQoLLreRNcFBBUEQAuvUv6Yw'
            })
            setCurrency(response.data)
            setLoading(false)
            console.log(response.data)
            console.log(response.data.filter((x) =>   listCurrency.includes(x['cc'])))
        } catch (err) {
            console.log(err)
        }
    }
  return (
    <div className="App">
      <Header
      USDUAH = {usduah}
      EURUAH = {euruah}
      />

    </div>
  );
}

export default App;
