import './App.scss';
import React from "react";
import Header from "./components/Header/Header";
import {useState} from "react";
import axios from "axios";



function App() {
    const [currency, setCurrency] = useState()
    const [loading, setLoading] = useState(true)

    React.useEffect(() => {
        getCurrency()


    }, [])
    async function getCurrency() {
        try {
            setLoading(true)
            let response = await axios.get('https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json')
            setCurrency(response.data)
            console.log(response.data.fil)
            setLoading(false)
        } catch (err) {
            console.log(err)
        }
    }
  return (
    <div className="App">
      <Header/>

    </div>
  );
}

export default App;
