import React, {useEffect, useState} from 'react';
import './converter.scss'
import Select from "../UI/Select/Select";
import MultipleAddonsExample from "../UI/MultipleAddonsExample/MultipleAddonsExample";
import arrows from '../imgs/21300.png'

const Numbers = ['1','2','3','4','5','6','7','8','9','0']


function Converter({getCurrency, Rate}) {
    const [to,setTo] = useState('UAH')
    const [from, setFrom] = useState('USD')
    const [cashTo, setCashTo] = useState('1')
    const [cashFrom, setCashFrom] = useState('1')
    const [revers, setRevers] = useState(false)

    useEffect(() =>{
        getCurrency(to, from,cashFrom,setCashTo)
}
        ,[to,from])


    function NewFrom(event) {
        console.log(event)
    }

    function NewCashFrom(event) {
        let number = cashFrom
        if(event === null){
            setCashFrom(cashFrom.slice(0,cashFrom.length -1 ))
             number = number.slice(0, number.length - 1)

        }
        else if (Numbers.includes(event)) {
            setCashFrom((perv) => perv + String(event))
             number = cashFrom + String(event)
        }
        getCurrency(to,from,number,setCashTo)
    }


    function NewTo(event) {
        setTo(event)

    }
    function NewCashTo(event) {
        let number = cashTo
        if(event === null ){

            setCashTo(cashTo.slice(0,cashTo.length -1 ))
            number = number.slice(0, number.length - 1)

        }
        else if (Numbers.includes(event)) {
            setCashTo((perv) => perv + String(event))
            number = cashTo + String(event)
        }
        getCurrency(from,to,number,setCashFrom)
    }
    function Revers() {
        setTo(from)
        setFrom(to)
        setRevers(!revers)
    }

    return (
        <div className={'converter'}>
            <div >
                <Select
                    Value={from}
                    NewValue={NewFrom}/>
                <MultipleAddonsExample
                    Cash={cashFrom}
                    NewCash={NewCashFrom}
                />
            </div>
            <div className={`arrows ${(revers) ? 'revers' : null}`}><img  src={arrows} onClick={Revers}/></div>
            <div>
                <Select
                    Value={to}
                    NewValue={NewTo}/>
                <MultipleAddonsExample
                    Cash={cashTo}
                    NewCash={NewCashTo}
                />
            </div>
        </div>
    );
}

export default Converter;
