import './App.scss';
import React from "react";
import Header from "./components/Header/Header";
import {useState} from "react";
import axios from "axios";
import Converter from "./components/Converter/Converter";

const listCurrency = ['USD', 'EUR','UAH']



function App() {
    const [rate, setRate] = useState('')
    const [loading, setLoading] = useState(true)

    const [usduah,setUsdUah] = useState(0);
    const [euruah, setEurUah] = useState(0);

    React.useEffect(() => {
        getUsdUah()
        getEurUah()
    }, [usduah,euruah])

    function getUsdUah() {

        getCurrency('uah', 'usd','1', setUsdUah)
    }
    function getEurUah() {
        getCurrency('uah', 'eur','1', setEurUah)
    }

    async function getCurrency(to,from, amount, func = setRate) {
        try {
            setLoading(true)
            let response = await axios.get(`https://api.apilayer.com/currency_data/convert?to=${to}&from=${from}&amount=${amount}`,{
                headers : 'apikey: AIUIvPfNpQoLLreRNcFBBUEQAuvUv6Yw'
            })
            func((response.data.result))
            setLoading(false)

        } catch (err) {
            console.log(err)
        }
    }
  return (
    <div className="App">
      <Header
      USDUAH = {usduah}
      EURUAH = {euruah}
      />
        <Converter
            getCurrency={getCurrency}
            Rate={rate}
            />

    </div>
  );
}

export default App;
